import React, { useEffect } from 'react';
import '../css/Home.scss';
import CyberopsLogo from '../assets/CYBER_OPS_LOGO-2.png';
import TeamCarsouel from '../components/teamCarsouel/TeamCarsouel';




function Home() {

  useEffect(() => {
    const script = document.createElement("script")
    script.innerHTML = `
    var swiper = new Swiper(".blog-slider", {
      spaceBetween: 30,
      effect: "fade",
      loop: true,
      // autoHeight: true,
      pagination: {
        el: ".blog-slider__pagination",
        clickable: true,
      },
    });`
    document.body.appendChild(script)
  }, [])



  return (
    <div className='homeContainer'>
      <div className='home'>    
        <div className='Heading'>
          <h1 className='MainHeading'> Welcome to <span>CyberOps</span> Club</h1>
          <p>Lorem ipsum dolor sit amet consectetur, adipisicing elit. In eos neque ex tempore doloribus aliquid optio eaque eum, odit, laboriosam voluptatem sunt modi mollitia sed beatae error corrupti corporis accusamus!</p>
          <img className='CyberopsLogo' src={CyberopsLogo} alt=""/>
          <button>Join the Club</button>
        </div>
      </div>

      <div className='Info'>
        <div className='InfoContainer'>
          <h1>Who are we?</h1>
          <p>Lorem ipsum dolor sit amet consectetur, adipisicing elit. In eos neque ex tempore doloribus aliquid optio eaque eum, odit, laboriosam voluptatem sunt modi mollitia sed beatae error corrupti corporis accusamus!</p>

          <h1>Our Mission</h1>
          <p>Lorem ipsum dolor sit amet consectetur, adipisicing elit. In eos neque ex tempore doloribus aliquid optio eaque eum, odit, laboriosam voluptatem sunt modi mollitia sed beatae error corrupti corporis accusamus!</p>
          <h1>CyperOps Future Plans</h1>
        </div>
      </div>

      <div className='team'>
        <div className='teamContainer'>
          <h1>Meet the team</h1>
          <div className='teamSwiper'>
            <TeamCarsouel />
          </div>
        </div>
      </div>

      <footer>
        <h1>CyberOps</h1>
      </footer>
    </div>
  );
}

export default Home;
